class PostComments{constructor(e){console.log("post comments"),this.postId=e,this.postContainer=$(`#post-${e}`),this.newCommentForm=$(`#post-${e}-comments-form`);let t=this;this.createComment(e),$(" .delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment=function(e){console.log(this);let t=this;this.newCommentForm.submit((function(o){o.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(o){console.log(o);let n=t.newCommentDom(o.data.comment);$(`#post-comments-${e}`).prepend(n),t.deleteComment($(" .delete-comment-button",n)),new ToggleLike($(" .toggle-like-button",n)),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))};newCommentDom=function(e){return console.log(e),console.log(e.content),$(`<li id="comment-${e._id}">\n                          <p>    \n                              <small>\n                                  \x3c!-- p.id is id of that comment --\x3e\n                                  <a class="delete-comment-button" href="/comments/destroy/${e._id}">Delete</a>\n                              </small>\n                              ${e.content}\n                              <br>\n                              <small>\n                              ${e.user.Name}\n                              </small>\n\n                              <small>\n                            \n                              <a class="toggle-like-button" data-likes="0" href="/likes/toggle/?id=${e._id}&type=Comment">\n                                  0 Likes\n                              </a>\n                          \n                          </small>\n                          \n                          </p>\n                      </li>              \n                  </ul>\n              </div>\n          </div>\n      </li>`)};deleteComment=function(e){console.log(e),$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){console.log(e),$(`#comment-${e.data.comment_id}`).remove(),new Noty({theme:"relax",text:"Comment Deleted!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}}